import createDataContext from "./createDataContext"
import AsyncStorage from "@react-native-community/async-storage"
import trackApi from "../Api/api"
const authReducer = (state, action) => {
	console.log('state test: ', state);
	switch (action.type) {
		case "signInError":
			return { ...state, singInError: action.error }
		case "singUpError":
			return { ...state, singUpError: action.error }
		case "signInSuccess":
			return { ...state, signInSuccess: action.token }

		default:
			return state;
	}

}


const signUp = dispatch => async (payload) => {
	try {
		await trackApi.post("/signup", payload, {}).then((response) => {

			// AsyncStorage.setItem("token", response.data.token)
			dispatch({ type: "singUpSuccess", token: response.data.token })

		}).catch((error) => {
			dispatch({
				type: "singUpError",
				error: error.response.data.message
			})
		});
	} catch (err) {

	}

}

const signIn = (dispatch,callback) => async (payload) => {
	try {
		await trackApi.post("/signin", payload, {}).then((response) => {

			// AsyncStorage.setItem("token", response.data.token)
			dispatch({ type: "signInSuccess", token: response.data.token })
			callback();
		}).catch((error) => {
			dispatch({
				type: "signInError",
				error: error.response.data.message
			})
		});
	} catch (err) {

	}

}

export const { Context, Provider } = createDataContext(authReducer, { signUp, signIn }, { isSingedIn: false })
